import React,{Component} from 'react';
import CardList from './CardList'
//
import userApi from '../Data/userApi.json'
import PropTypes from 'prop-types'
import InputField from './InputField'
import OutputField from './outputField'

class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cards: [],
            isHide:false,
            //strJSON:'',
            strLog:''
        }
        //this.taRef = React.createRef();
        this.removeCard=this.removeCard.bind(this)
        this.setCardsParseJSON=this.setCardsParseJSON.bind(this)
    }

    // static defaultProps={
    //     cards:[],
    //     isHide:false,
    //     strLog:''
    // }

    static propTypes = {
        cards:PropTypes.array,
        isHide:PropTypes.bool

    }
    // componentDidMount(){
    //     console.log(this.refs._ref.value);
    // }


    // shouldComponentUpdate(nextState){
    //     const { cards } = this.nextState
    //     return cards !== nextState.cards
    // }

    // componentWillReceiveProps(nextProps){

    // }

    //Косяки!!!!! 
    setCardsParseJSON(value){
        //const {value} = this.refs._ref
        //console.log(value);

        if(IsJsonString(value)){
            const newcards = JSON.parse(value)
            const {cards} = this.state
            if(Array.isArray(newcards))
                this.setState({newcards})
            else
            {
                const newcards = this.state.cards.push(cards)
                console.dir(newcards);
                this.setState({cards:newcards})
            }

            //value=''
        }
        else if(value!=='')
        {
            this.setState({strLog:'Не верный формат!'})
        }
        else
            this.setState({strLog:''})     
    }

    removeCard(id)
    {
        const cards = this.state.cards.filter(
            card=>card.id !== id
        )
        this.setState({cards})
    }

    render() {
        const {removeCard, setCardsParseJSON}=this
        const { cards, strLog } = this.state
        //const { _ta } = this.refs
        return(
            <div>
                <InputField setCardsParseJSON={setCardsParseJSON}/>
                <div>{strLog}</div>
                <CardList list={cards} onRemove={removeCard}/>
                <button>Add user</button>
                <OutputField/>
            </div>

        )
    }
}

function IsJsonString(str) {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}

export default App;
