class AddCard extends Component{

    constructor(props){
        super(props)
        this.state={
            isAddMode:false
        }
    }



    render(){
        const {isAddMode} = this.state
        return(
            <div >
                <div hidden={isAddMode}>
                    <button>Add</button>
                </div>
                <div hidden={!isAddMode}>

                </div>
            </div>
        )

    }


}
