import React from 'react'
import User from '../User'
import './style.css'
import AddCard from '../AddUser'
import RemoveUser from '../../Redux/Actions/RemoveUser'
import switchEditMode from '../../Redux/Actions/SwitchEditMode'
import clearUsers from '../../Redux/Actions/ClearUsers'

const UsersList = ({store})=>{
    //const { value, isEdit } = store.getState().Users
    const {Users} = store.getState()
    //console.dir(Users);
    //const sortedColors = [...colors].sort(sortFunction(sort))
    const onClearUsers= ()=>{
        store.dispatch(
            clearUsers()
        )
    }
    //{console.log(Users[0].isEdit)}
    return (
        <div>
            <div>Users:</div>
            <div className='list-cards'>
                {(Users.length===0)?
                    <p>Нет данных</p>:
                    Users.map(user=>
                        <User key={user.id}  {...user} store={store} isEdit={user.isEdit}

                        onRemove={() =>
                            store.dispatch(
                                    RemoveUser(user.id)
                                )
                            } 



                        // onSave = {(value)=>
                        //     store.dispatch(

                        //     )
                        // }
                        />
                    )
                }
            </div>
            <button className='btn' onClick={onClearUsers} hidden={Users.length===0}>Clear</button>
        </div>
    )

}
