import React,{Component} from 'react';
import CardList from './CardList'
import userApi from '../Data/userApi.json'
import PropTypes from 'prop-types'
import InputField from './InputField'
class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cards: userApi,
            isHide:false,
            //strJSON:'',
            strLog:''
        }
        //this.taRef = React.createRef();
        this.removeCard=this.removeCard.bind(this)
        this.setCardsParseJSON=this.setCardsParseJSON.bind(this)
    }

    // static defaultProps={
    //     cards:[],
    //     isHide:false,
    //     strLog:''
    // }

    static propTypes = {
        cards:PropTypes.array,
        isHide:PropTypes.bool

    }
    // componentDidMount(){
    //     console.log(this.refs._ref.value);
    // }

    setCardsParseJSON(value){
        //const {value} = this.refs._ref
        console.log(value);

        if(IsJsonString(value)){
            const cards = JSON.parse(value)
            console.log(cards);
            if(Array.isArray(cards))
                this.setState({cards})
            else
                this.setState({cards:[cards]})
            //value=''
        }
        else if(value!='')
        {
            this.setState({strLog:'Не верный формат!'})
        }
        else
            this.setState({strLog:''})



    }

    // inputLog(value){

    // }

    removeCard(id)
    {
        const cards = this.state.cards.filter(
            card=>card.id !== id
        )
        this.setState({cards})
    }

    render() {
        const {removeCard, setCardsParseJSON}=this
        const { cards, strLog } = this.state
        //const { _ta } = this.refs
        return(
            <div>
                <InputField setCardsParseJSON={setCardsParseJSON}/>
                <div>{strLog}</div>
                {/* <textarea ref='_ref' onInput={setCardsParseJSON} name="comment" cols="40" rows="3"></textarea> */}
                <CardList cards={cards} onRemove={removeCard}/>
                <button>Add user</button>
            </div>

        )
    }
}

function IsJsonString(str) {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}

export default App;
