import React,{Component} from 'react';
import './style.css'

class Color extends Component{

    constructor(props){
        super(props)
        this.state = {
            isEdit:false
        }

        this.onEdit=this.onEdit.bind(this)
        this.onSave=this.onSave.bind(this)
    }

    getId = (node)=>{this.idField=node}
    getName= (node)=>{this.nameField=node}

    onEdit(){
        const {onSave} = this.props
        this.setState({isEdit:true})
    }

    onSave(){
        const {onSave} = this.props
        this.setState({isEdit:false})


    }


    render(){
        const {onEdit, onSave} = this
        const {isEdit} = this.state
        const {id, name, email, config_bool, timer_integer, flags, dateactivate, dateupdate, onRemove} = this.props
        return(
            <div className='card-user'>
                <div className='item'>
                    <div hidden={isEdit}>{id}</div>
                    <input type="text" size='5' hidden={!isEdit}></input>
                </div>
                <div className='item'>
                    <div hidden={isEdit}>{name}</div>
                    <input type="text" size='5' hidden={!isEdit}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{email}</div>
                    <input type="text" size='10' hidden={!isEdit}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{config_bool}</div>
                    <input type="text" size='5' hidden={!isEdit}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{timer_integer}</div>
                    <input type="text" size='5' hidden={!isEdit}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{flags}</div>
                    <input type="text" size='5' hidden={!isEdit}></input>
                </div>
                <div className='item' >
                    <div>{dateactivate}</div>
                    {/* <input type="text" size='15'></input> */}
                </div>
                <div className='item' >
                    <div>{dateupdate}</div>
                    {/* <input type="text" size='15'></input> */}
                </div>
                <button className='item' onClick={isEdit?onSave:onEdit}>{isEdit?'Save':'Edit'}</button> 
                <button className='item' onClick={onRemove}>Delete</button>
            </div>
        )
    }
}

function buildState() {

}

