import React,{Component} from 'react'


class InputField extends Component{

    constructor(props) {
        super(props)
    }
    getInputRef = (node)=>{this._ref=node};
    render(){
        const {getInputRef} = this
        const {setCardsParseJSON} = this.props
        const {value} = this.getInputRef
        return(
            <div>
                <textarea ref={getInputRef} onInput={()=>setCardsParseJSON(value)} name="comment" cols="40" rows="3"></textarea>
            </div>
        )
    }


}

