const User = ({store, id, name, email, config_bool, timer_integer, flags, dateactivate, dateupdate, onRemove, isEdit, onEdit, onSave, onCancel})=>{
    console.dir(onRemove)
    return (
        <div className='card-user'>
                 <div className='item'>
                    <div>{id}</div>
                 </div>
                 <div className='item'>
                    <div hidden={isEdit}>{name}</div>
                    <input  type="text" size='5' hidden={!isEdit}></input>
                 </div>
                 <div className='item' >
                    <div hidden={isEdit}>{email}</div>
                    <input type="text" size='10' hidden={!isEdit}></input>
                 </div>
                 <div className='item' >
                    <div hidden={isEdit}>{config_bool}</div>
                    <input  type="text" size='5' hidden={!isEdit}></input>
                 </div>
                 <div className='item' >
                    <div hidden={isEdit}>{timer_integer}</div>
                    <input type="text" size='5' hidden={!isEdit}></input>
                 </div>
                 <div className='item'>
                    <div hidden={isEdit}>{flags}</div>
                    <input  type="text" size='5' hidden={!isEdit}></input>
                 </div>
                 <div className='item' >
                     <div hidden={isEdit}>{dateactivate}</div>
                 </div>
                 <div className='item' >
                     <div hidden={isEdit}>{dateupdate}</div>
                 </div>
                 <button className='item' onClick={isEdit?onSave:onEdit}>{isEdit?'Save':'Edit'}</button> 
                 <button className='item' onClick={isEdit?onCancel:onRemove}>{isEdit?'Cancel':'Delete'}</button>
             </div>
    )
}

