import React,{Component} from 'react'


class InputField extends Component{

    constructor(props) {
        super(props)
    }
    getInputRef = (node)=>{this._ref=node};
    render(){
        const {setCards} = this.props.setCardsParseJSON
        const {value} = this.refs._ref
        return(
            <div>
                <textarea ref={this.getInputRef} onInput={()=>setCards(value)} name="comment" cols="40" rows="3"></textarea>
            </div>
        )
    }


}

