import React,{Component} from 'react';
import './style.css'

class Color extends Component{

    constructor(props){
        super(props)
        this.state = {
            isEdit:false
        }

        this.onEdit=this.onEdit.bind(this)
        this.onSave=this.onSave.bind(this)
    }

    getIdref = (node)=>{this.idField=node}
    getNameref= (node)=>{this.nameField=node}
    getEmailref= (node)=>{this.emailField=node}
    getConfigref= (node)=>{this.configField=node}
    getTimerref= (node)=>{this.timerField=node}
    getflagsref= (node)=>{this.flagsField=node}

    onEdit(){
        const {onSave} = this.props
        this.setState({isEdit:true})
    }

    onSave(){
        //const {id} = this.props
        const {id, onSave} = this.props
        const {idField, nameField, emailField, configField,timerField,flagsField} = this
        console.log(1)
        onSave({
            id:id,
            name:nameField.value,
            email:emailField.value,
            config_bool:configField.value,
            timer_integer:timerField.value,
            flags:flagsField.value,
            dateactivate:new Date().toGMTString(),
            dateupdate:new Date().toGMTString()
        })
        console.log(3)
        this.setState({isEdit:false})
    }


    render(){
        //const {nameField, emailField, configField, timerField, flagsField} = this
        const {onEdit, onSave, getIdref, getNameref, getEmailref, getConfigref, getTimerref, getflagsref} = this
        const {isEdit} = this.state
        const {id, name, email, config_bool, timer_integer, flags, dateactivate, dateupdate, onRemove} = this.props
        return(
            <div className='card-user'>
                <div className='item'>
                    <div >{id}</div>
                    {/* <input ref={getIdref} type="text" size='5' hidden={!isEdit}></input> */}
                </div>
                <div className='item'>
                    <div hidden={isEdit}>{name}</div>
                    <input ref={getNameref} type="text" size='5' hidden={!isEdit} value={name}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{email}</div>
                    <input ref={getEmailref} type="text" size='10' hidden={!isEdit} value={email}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{config_bool}</div>
                    <input ref={getConfigref} type="text" size='5' hidden={!isEdit} value={config_bool}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{timer_integer}</div>
                    <input ref={getTimerref} type="text" size='5' hidden={!isEdit} value={timer_integer}></input>
                </div>
                <div className='item' >
                    <div hidden={isEdit}>{flags}</div>
                    <input ref={getflagsref} type="text" size='5' hidden={!isEdit} value={flags}></input>
                </div>
                <div className='item' >
                    <div>{dateactivate}</div>
                    {/* <input type="text" size='15'></input> */}
                </div>
                <div className='item' >
                    <div>{dateupdate}</div>
                    {/* <input type="text" size='15'></input> */}
                </div>
                <button className='item' onClick={isEdit?onSave:onEdit}>{isEdit?'Save':'Edit'}</button> 
                <button className='item' onClick={onRemove}>Delete</button>
            </div>
        )
    }
}

function buildState() {

}

