import React,{Component} from 'react';
import CardList from './CardList'
//
import userApi from '../Data/userApi.json'
import PropTypes from 'prop-types'
import InputField from './InputField'
import OutputField from './outputField'


class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cards: userApi,
            isHide:false
        }
        this.removeCard=this.removeCard.bind(this)
        this.setCards=this.setCards.bind(this)
        this.addCard=this.addCard.bind(this)
        this.changeCard=this.changeCard.bind(this)
    }

    static propTypes = {
        cards:PropTypes.array,
        isHide:PropTypes.bool
    }

    setCards(card){
        const {addCard} = this
        const {cards} = this.state
        if(Array.isArray(card)) //тут косяк. Надо доплнять массив, а не подменять) 
            this.setState({cards:card})
        else
        {
            if(!IsConcatId(card,cards))
                addCard(card, cards)
        }
    }

    // outputCard(){
    //     const {cards} = this.state
    // }
    //JSON.stringify

    // componentDidMount(){
    //     console.log(this.refs._ref.value);
    // }


    // shouldComponentUpdate(nextState){
    //     const { cards } = this.nextState
    //     return cards !== nextState.cards
    // }

    // componentWillReceiveProps(nextProps){

    // }
    GeneratorId() {
        const {cards} = this.state
        let id = cards.slice(-1).id

        return 
    }
    addCard(value, arr)
    {
        this.setState({cards:[...arr,value]})
    }

    changeCard(value)
    {
        //console.log(2)
        //console.dir(value)
        const {cards} = this.state

        for (let card of cards)
        {
            //console.log(value)
            if(card.id==value.id)
            {
                //console.dir(card)
                for (let key in card){
                    card[key]=value[key]
                    //console.log(`card: ${card[key]} = value: ${value[key]}`)
                }
                this.setState({cards})
            }
        }
    }

    removeCard(id)
    {
        const cards = this.state.cards.filter(
            card=>card.id !== id
        )
        this.setState({cards})
    }

    render() {
        const {removeCard, setCards, changeCard}=this
        const { cards } = this.state
        //const { _ta } = this.refs
        return(
            <div>
                <InputField isHide={false} onSetCards={setCards}/>
                <CardList list={cards} onRemove={removeCard} isHide={cards.length>0?false:true} Save = {changeCard} />

                <OutputField isHide={cards.length>0?false:true} value={cards}/>
            </div>

        )
    }
}



function IsConcatId(obj,arr)
{   
    for (let elem of arr)
    {
        if(obj.id==elem.id)
            return true
    }
        return false
}



export default App;
