import React,{Component} from 'react';
import CardList from './CardList'
//
import userApi from '../Data/userApi.json'
import PropTypes from 'prop-types'
import InputField from './InputField'
import OutputField from './outputField'

class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cards: [],
            isHide:false
        }
        this.removeCard=this.removeCard.bind(this)
        this.setCards=this.setCards.bind(this)
        this.addCard=this.addCard.bind(this)
    }

    static propTypes = {
        cards:PropTypes.array,
        isHide:PropTypes.bool
    }

    setCards(card){
        const {addCard} = this
        const {cards} = this.state
        if(Array.isArray(card)) //тут косяк. Надо доплнять массив, а не подменять) 
            this.setState({cards:card})
        else
        {
            if(!IsConcatId(card,cards))
                addCard(card, cards)
        }
    }

    // outputCard(){
    //     const {cards} = this.state
    // }
    //JSON.stringify

    // componentDidMount(){
    //     console.log(this.refs._ref.value);
    // }


    // shouldComponentUpdate(nextState){
    //     const { cards } = this.nextState
    //     return cards !== nextState.cards
    // }

    // componentWillReceiveProps(nextProps){

    // }

    addCard(value, arr)
    {
        this.setState({cards:[...arr,value]})
    }

    editCard(value)
    {
        const {cards} = this.state

        for (let card of cards)
        {
            if(card.id==value.id)
            {
                for (key in obj){
                    card[key]=value[key]
                }
            }
        }
    }

    removeCard(id)
    {
        const cards = this.state.cards.filter(
            card=>card.id !== id
        )
        this.setState({cards})
    }

    render() {
        const {removeCard, setCards}=this
        const { cards, strLog } = this.state
        //const { _ta } = this.refs
        return(
            <div>
                <InputField isHide={false} onSetCards={setCards}/>
                <CardList list={cards} onRemove={removeCard} isHide={cards.length>0?false:true} />
                <OutputField isHide={cards.length>0?false:true} value={cards}/>
            </div>

        )
    }
}

function IsConcatId(obj,arr)
{   
    for (let elem of arr)
    {
        if(obj.id==elem.id)
            return true
    }
        return false
}



export default App;
