import React,{Component} from 'react';

class PatternCard extends Component{




    render(){
        return(
            <div>
                <div className='card-user'>
                    <div className='item'>
                        <input ref={getNameref} type="text" size='5' hidden={!isEdit}></input>
                    </div>
                    <div className='item'>
                        <input ref={getNameref} type="text" size='5' hidden={!isEdit}></input>
                    </div>
                    <div className='item'>
                        <input ref={getEmailref} type="text" size='10' hidden={!isEdit}></input>
                    </div>
                    <div className='item'>
                        <input ref={getConfigref} type="text" size='5' hidden={!isEdit}></input>
                    </div>
                    <div className='item'>
                        <input ref={getTimerref} type="text" size='5' hidden={!isEdit}></input>
                    </div>
                    <div className='item'>
                        <input ref={getflagsref} type="text" size='5' hidden={!isEdit}></input>
                    </div>
                    <div className='item'>
                        <div>{dateactivate}</div>
                    </div>
                    <div className='item'>
                        <div>{dateupdate}</div>
                    </div>
                    <button className='item' onClick={isEdit?onSave:onEdit}>{isEdit?'Save':'Edit'}</button> 
                    <button className='item' onClick={isEdit?onСancel:onRemove}>{isEdit?'Cancel':'Delete'}</button>
                </div>
            </div>
        )
    } 
}


class AddCard extends Component{

    constructor(props){
        super(props)
        this.state={
            isAddMode:false
        }
        this.openAddMode=this.openAddMode.bind(this)
    }

    openAddMode(){
        this.setState({isAddMode:true})
    }


    render(){
        const {isAddMode} = this.state
        const {openAddMode} = this
        return(
            <div >
                <div hidden={isAddMode}>
                    <button onClick={openAddMode}>Add</button>
                </div>
                <div hidden={!isAddMode}>
                    <PatternCard/>
                </div>
            </div>
        )

    }


}
