import React,{Component} from 'react';
import CardList from './CardList'
import userApi from '../Data/userApi.json'
import PropTypes from 'prop-types'

class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cards: userApi,
            isHide:false,
            strJSON:''
        }
        this.taRef = React.createRef();
        this.removeCard=this.removeCard.bind(this)
        this.setCardsParseJSON=this.setCardsParseJSON.bind(this)
    }

    static defaultProps={
        cards:[],
        isHide:false
    }

    static propTypes = {
        cards:PropTypes.array,
        isHide:PropTypes.bool

    }
    componentDidMount(){
        console.log(this.refs._ref.value);
    }

    setCardsParseJSON(){
        // const { _ta } = this.refs
        //console.log(taRef)
        const {value} = this.refs._ref
        if(value !== 'undefined')
        {
            const cards = JSON.parse(value)
            if(Array.isArray(cards))
                this.setState({...cards})
            else
                this.setState({...cards.push(cards)})
        }


    }

    removeCard(id)
    {
        const cards = this.state.cards.filter(
            card=>card.id !== id
        )
        this.setState({cards})
    }

    render() {
        const {removeCard, setCardsParseJSON}=this
        const { cards } = this.state
        //const { _ta } = this.refs
        return(
            <div>

                <textarea innerRef={(textarea) => { this.messageTextarea = textarea }} onInput={setCardsParseJSON} name="comment" cols="40" rows="3"></textarea>
                <div>test</div>
                <CardList cards={cards} onRemove={removeCard}/>
                <button>Add user</button>
            </div>

        )
    }
}

export default App;
