import React,{Component} from 'react';
import CardList from './CardList'
import userApi from '../Data/userApi.json'
import PropTypes from 'prop-types'

class App extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cards: userApi,
            isHide:false,
            strJSON:''
        }
        //this.taRef = React.createRef();
        this.removeCard=this.removeCard.bind(this)
        this.setCardsParseJSON=this.setCardsParseJSON.bind(this)
    }

    static defaultProps={
        cards:[],
        isHide:false
    }

    static propTypes = {
        cards:PropTypes.array,
        isHide:PropTypes.bool

    }
    // componentDidMount(){
    //     console.log(this.refs._ref.value);
    // }

    setCardsParseJSON(){
        const {value} = this.refs._ref
        console.log(value);

        if(IsJsonString(value)){
            let data = JSON.parse(value)
            console.log(data);
            if(Array.isArray(data))
                this.setState({data})
            else
            {
                this.setState({cards:[data]})
            }
        }
        else{
            console.log('Что за хуйню ты ввел?');
        }


    }

    removeCard(id)
    {
        const cards = this.state.cards.filter(
            card=>card.id !== id
        )
        this.setState({cards})
    }

    render() {
        const {removeCard, setCardsParseJSON}=this
        const { cards } = this.state
        //const { _ta } = this.refs
        return(
            <div>

                <textarea ref='_ref' onInput={setCardsParseJSON} name="comment" cols="40" rows="3"></textarea>
                <div>test</div>
                <CardList cards={cards} onRemove={removeCard}/>
                <button>Add user</button>
            </div>

        )
    }
}

function IsJsonString(str) {
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}

export default App;
